import React, { useContext } from 'react'
import { AuthContext } from '../../auth/AuthContext'

export const LoginPage = ({ history }) => {

  const {user, dispatch} = useContext(AuthContext);

  const handleLogin = () => {
    //history.replace('/');
    const action = {
      type: '[auth] login',
      payload: {
        logged: true
      }
    }

    dispatch(action);

  }

  return (
    <div className='container mt-5'>
        <h1> Login </h1>
        <hr/>

        <button
          className='btn btn-success'
          onClick={ handleLogin }
        >
          Ingresar
        </button>
    </div>
  )
}
