import React, { Fragment } from 'react'

export const HeroCardModal = ({characters, show=false}) => {

  return (
    <Fragment>
        {
            show && (
                <div className="modal">
                    <div className="modal-dialog modal-sm">
                        <div className="modal-content">
                            {characters}
                        </div>
                    </div>
                </div>
            )
        }
    </Fragment>
  )
}
