import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import { HeroCardModal } from './HeroCardModal';

export const HeroCard = ({hero}) => {
    const {id, superhero, publisher, alter_ego, first_appearance, characters} = hero;
    const [showModal, setShowModal] = useState(false);

    return (
        <React.Fragment>
            <div className='card ms-3' style={{ maxWidth: '540px'}}>
                <div className='row no-gutters'>
                    <div className='col-md-4'>
                        <img src={`./assets/heroes/${id}.jpg`} alt={"hero-img"} className="card-img"/>
                    </div>
                    <div className='col-md-8'>
                        <div className='card-body'>
                            <h5 className='card-title'> { superhero } </h5>
                            <p className='card-text'> { alter_ego } </p>
                            {
                                alter_ego !== characters && (
                                    <button
                                    className='btn btn-sm btn-outline-secondary'
                                    onClick={ () => setShowModal(true) }
                                    >
                                        Ver otros nombres
                                    </button>
                                )
                            }
                            <p className='card-text'>
                                <small className='text-muted'> { first_appearance } </small>
                            </p>
                        </div>
                    </div>
                </div>
                <div>
                    <Link to="">
                        <button className='btn btn-block btn-secondary'> Ver m√°s </button>
                    </Link>
                </div>
            </div>
            {
                showModal && (<HeroCardModal characters={characters}/>)
            }
        </React.Fragment>

    )
    //<button type="button" class="btn btn-primary" data-toggle="modal" data-target=".bd-example-modal-sm">Small modal</button>
}
