import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import {PropTypes} from 'prop-types';

export const HeroCard = ({hero, mouseState}) => {
    const [onMouseOverState, setOnMouseOverState] = useState('');
    const {id, superhero, publisher, alter_ego, first_appearance, characters} = hero;
    const chopped_string = characters?.slice(0,10);

    const handleMouseOver = () => {
        onMouseOverState === '' ? (
            setOnMouseOverState('border-info')
        ): (
            setOnMouseOverState('')
        )
    }

    return (
        <div className={`card ms-3 ${mouseState}`} onMouseOver={ handleMouseOver } style={{ maxWidth: '540px'}}>
            <div className='row no-gutters'>
                <div className='col-md-4'>
                    <img src={`./assets/heroes/${id}.jpg`} alt={"hero-img"} className="card-img"/>
                </div>
                <div className='col-md-8'>
                    <div className='card-body'>
                        <h5 className='card-title'> { superhero } </h5>
                        {
                            alter_ego === characters ? (
                                <p className='card-text'> { alter_ego } </p>
                            ) : (
                                <p className='card-text'> { chopped_string }... </p>
                            )
                        }
                        <p className='card-text'>
                            <small className='text-muted'> { first_appearance } </small>
                        </p>
                    </div>
                </div>
            </div>
            <div>
                <Link to={`./heroe/${id}`}>
                    <button className='btn btn-block btn-secondary'> Ver m√°s </button>
                </Link>
            </div>
        </div>
    )
}
